import { useState } from "react";

const ListItem = ({name,description}) => {

    const [Showdescription, setShowtdescripion] = useState(false);

    if(description===""){
        description = "Esta tarea no tiene una descripción"
    }

    const showHandler = () => {
        setShowtdescripion(!Showdescription);
    }
    const completedHandler= () => {
        console.log("buttton completed clicked")
    }

    return(
        <div key={name} className="block max-w-sm p-6 border border-gray-200 rounded-lg shadow hover:bg-gray-100 bg-gray-600 border-gray-700 hover:bg-gray-500 my-5" onClick={showHandler}>
            <h5 className="mb-2 text-2xl font-bold tracking-tight text-white  transition ease-out hover:scale-110  duration-300" >{name}</h5>
            {Showdescription? <p className="font-normal text-lg text-gray-200">{description}</p> : null}
            <button onClick={completedHandler} > buttom completed</button>
        </div>
    )

}

export default ListItem;
