import { useState } from "react";
import Swal from 'sweetalert2'

const Form = ({setTodos,Todos}) =>{
    const [Todo, setTodo] = useState("");

    const TodoHandler = (e) =>{
        setTodo(e.target.value);
    }

    const addTodo = (e) =>{
        e.preventDefault();

        if(Todo === "" || Todo.length<3){
            Swal.fire({
                icon: 'error',
                title: 'Campos vacios o ToDo muy corto',
              })
              return
        }
       setTodos(Todo);
    }

    return(
        <div>
            <div className="flex flex-wrap -mx-3 mb-6">
                <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                <label className="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2">
                    Nombre de Tarea
                </label>
                <input className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight" type="text" onChange={TodoHandler} value={Todo}/>
                </div>
                <div className="w-full md:w-1/2 px-3">
                <label className="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2">
                    Descripci√≥n (opcional)
                </label>
                <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none" type="text"/>
                </div>
            </div>
            {/* <input className="bg-gray-200 mr-5 text-center" type="text" onChange={TodoHandler} value={Todo} /> */}
            <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded"
                    onClick={addTodo}>
                        Agregar
            </button>
        </div>

    );
}

export default Form;
