import { useState } from "react";

const ListItem = ({name,description}) => {

    const [Showdescription, setShowtdescripion] = useState(false);

    if(description===""){
        description = "Esta tarea no tiene una descripción"
    }

    const showHandler = () => {
        setShowtdescripion(!Showdescription);
    }
    const completedHandler= () => {
        console.log("buttton completed clicked")
    }

    const deleteHandler = () => {
        console.log(`La tarea ${name} se ha eliminado`);
    }

    return(
        <div key={name} className="block max-w-sm p-6 border border-gray-200 rounded-lg shadow hover:bg-gray-100 bg-gray-600 border-gray-700 hover:bg-gray-500 my-5" >
            <h5 className="mb-2 text-2xl font-bold tracking-tight text-white  transition ease-out hover:scale-110  duration-300"onClick={showHandler} >{name}</h5>
            {Showdescription? <p className="font-normal text-lg text-gray-200 my-3">{description}</p> : null}
            <div className="flex justify-between">
                <div className="flex items-center" onClick={completedHandler}>
                    <input   type="checkbox" value="" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"/>
                    <label className="ml-2 text-sm font-medium text-gray-300">Finalizar tarea</label>
                </div>
                <div>
                    <button className="bg-rose-500 hover:bg-rose-700 text-white font-bold py-2 px-4 border border-rose-700 rounded" onClick={deleteHandler}>Eliminar</button>
                </div>
            </div>
        </div>
    )

}

export default ListItem;
