    }

    const addTodo = (e) =>{
        e.preventDefaul();
    }

    return(
        <div className="flex justify-between">
            <input className="bg-gray-200 mr-5 text-center" type="text" onChange={TodoHandler} value={Todo} />
            <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded">Agregar</button>
        </div>

    );
}

export default Form;
