import { useEffect, useState } from "react";
import Form from "./Components/Form";
import List from "./Components/List";
import { GetTodos } from "./helpers/GetTodos";


function App() {

  const [Todos, setTodos] = useState([{
    name:"do something",
    description: `scelerisque varius velit suscipit eu. Morbi erat orci, consectetur in orci ut,
    commodo tincidunt nisi. Proin pharetra semper ligula in gravida.`,
    status: true ,
  }]);

  const GetAllTodos = async() =>{
    const newTodos = await GetTodos();
    console.log(newTodos);
    setTodos(...Todos,newTodos);
  }

  useEffect(() => {
    GetAllTodos();
  }, [])


  return (
    <div className="mt-10">
      <h1 className="text-5xl font-bold text-center" >To-DoApp</h1>
      <div className="mt-20 flex justify-evenly items-start">
        <Form setTodos={setTodos} Todos= {Todos} />
        <List Todos={Todos} />
      </div>
    </div>
  );
}

export default App;
