const ListItem = ({name,description = "Esta tarea no cuenta con una descripción"}) => {

    return(
        <div key={name} className="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700 my-5">
            <h5 className="mb-2 text-xl font-bold tracking-tight text-gray-900 dark:text-white">{name}</h5>
            <p className="font-normal text-gray-700 dark:text-gray-400">{description}</p>
        </div>
    )

}

export default ListItem;
