import { useContext, useEffect, useState } from "react";
import Swal from 'sweetalert2'
import { AddTodoByUser } from "../helpers/AddTodoByUser";
import { v4 as uuidv4 } from 'uuid';
import { TodosContext } from "../App";

const Form = () =>{
    // state para el nombte y la descripción
    const [NewTodo, setNewTodo] = useState({
        name:"",
        description:""
    });


    const {Todos,setTodos,IsEditTodo,EditTodo} = useContext(TodosContext);
    console.log(EditTodo);

    useEffect(() => {
      setNewTodo({
        name: EditTodo.name,
        description: EditTodo.description
      })
    }, [EditTodo])

    //handler para obtener y guardar el resultado del nombre y la descripción
    const TodoNameHandler = (e) =>{
        setNewTodo({
            name:e.target.value,
            description: NewTodo.description
    });
    }

    const TodoDescriptHandler = (e) =>{
        setNewTodo({
          name: NewTodo.name,
          description: e.target.value
        });
    }

    //funcion para agregar un todo
    const addTodo = async(e) =>{
        e.preventDefault();

        //validación al nombre del todo
        if(NewTodo.name === "" || NewTodo.name.length<3){
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text:"El Nombre de la Tarea es muy Corto"
              })
              return
        }
        const {id} = await AddTodoByUser(NewTodo.name);
        console.log(id);

        setTodos((current) =>
            current.filter((todo) => todo.id !== EditTodo.id)
        );
        // agregar el todo al arreglo principal
       setTodos(
        [
            {
                name:NewTodo.name,
                description:NewTodo.description,
                status:false,
                id: uuidv4()
            }
        ,...Todos]);
        setNewTodo({
            name:"",
            description:""
        })
        Swal.fire({
            position: 'center',
            icon: 'success',
            title: `La Tarea ha sido creada correctamente con el id ${id}`,
            showConfirmButton: false,
            timer: 1700
          })
    }

    return(
        <div className="mx-5">
            <div className="flex flex-wrap -mx-3 mb-6">
                <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                <label className="uppercase tracking-wide text-gray-700 text-white text-xs font-bold mb-5">
                    Nombre de Tarea
                </label>
                <input className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight my-3" type="text" onChange={TodoNameHandler} value={NewTodo.name || ""}/>
                </div>
                <div className="w-full md:w-1/2 px-3">
                <label className="uppercase tracking-wide text-white text-gray-700 text-xs font-bold ">
                    Descripción (opcional)
                </label>
                <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 my-3 leading-tight" type="text" onChange={TodoDescriptHandler} value={NewTodo.description || ""} />
                </div>
            </div>
            {IsEditTodo? 
                <button className="bg-orange-400 hover:bg-orange-500 text-white font-bold py-2 px-4 border border-orange-700 rounded"
                    onClick={EditTodo}>
                    Editar
                </button>
            : 
                <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded"
                    onClick={addTodo}>
                    Agregar
                </button>
            }

        </div>

    );
}

export default Form;
