import { useState } from "react";

const ListItem = ({name,description}) => {

    const [Showdescription, setShowtdescripion] = useState(false);

    if(description===""){
        description = "Esta tarea no tiene una descripción"
    }

    const showHandler = () => {
        setShowtdescripion(!Showdescription);
    }
    const completedHandler= () => {
        console.log("buttton completed clicked")
    }

    return(
        <div key={name} className="block max-w-sm p-6 border border-gray-200 rounded-lg shadow hover:bg-gray-100 bg-gray-600 border-gray-700 hover:bg-gray-500 my-5" >
            <h5 className="mb-2 text-2xl font-bold tracking-tight text-white  transition ease-out hover:scale-110  duration-300"onClick={showHandler} >{name}</h5>
            {Showdescription? <p className="font-normal text-lg text-gray-200 my-5">{description}</p> : null}
            <div class="flex items-center" onClick={completedHandler}>
                <input checked id="checked-checkbox" type="checkbox" value="" class="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"/>
                <label for="checked-checkbox" class="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">Checked state</label>
            </div>
        </div>
    )

}

export default ListItem;
